import React, { useState, useEffect } from 'react'
import './index.scoped.scss'

import request from '../../utils/request'
import { Comment, Tooltip, Avatar, Rate, Image } from 'antd'
import moment from 'moment'

import { noImage } from '../../utils'

// 防止噴錯卡住 ref: https://www.c-sharpcorner.com/blogs/error-handling-while-use-image-render-in-react-js-application
function CustomImg(props) {
  const { src, alt = 'imaghe', ...otherProps } = props
  let imagePath = ''
  try {
    imagePath = require('../../img/' + src)
  } catch (err) {
    imagePath = noImage //set default image path
  }

  return <Avatar {...otherProps} src={imagePath} alt={alt} />
}

function Commentt() {
  const [commentt, setCommentt] = useState([])

  async function getCommentt() {
    const response = await request({
      url: `/members/getCommentt`,
      method: 'POST',
    })

    const { success, data } = response

    if (success && data) {
      setCommentt(data)
    }
  }

  useEffect(() => {
    getCommentt()
  }, [])

  return (
    <>
      {commentt.length > 0 ? (
        commentt.map((item, index) => {
          return (
            <Comment
              key={index}
              className="commentt_container"
              author={
                <div>
                  <div>{item.buy_product}</div>
                  <Rate disabled defaultValue={item.stars} />
                </div>
              }
              avatar={<CustomImg src={item.avatar} />}
              content={
                <div>
                  <p>{item.review_comment}</p>
                  <div style={{ marginTop: '15px' }}>
                    <Image
                      width={300}
                      alt="img"
                      onError={(e) => {
                        e.target.style.width = '150px' // 加載錯誤重新設定圖片大小(太大了)
                        e.target.onerror = null
                      }}
                      src={`${window.location.origin}/img/${item.photo}`}
                      fallback="data:image/png;base64,"
                    />

                    {/* 一樣效果，但沒有加載失敗的替代圖
                      <img
                        style={{ width: '300px' }}
                        alt={item.photo}
                        src={`${window.location.origin}/img/${item.photo}`}
                      />
                    */}
                  </div>
                </div>
              }
              datetime={
                <Tooltip title={moment().format('YYYY-MM-DD HH:mm')}>
                  <span>{moment().format('YYYY-MM-DD')}</span>
                </Tooltip>
              }
            />
          )
        })
      ) : (
        <Comment className="commentt_container" content={<p>尚未評論</p>} />
      )}
    </>
  )
}

export default Commentt
